const { Layout, Row, Col, Form, Input, Button, Alert, Image } = require("antd");
import { useRouter } from "next/router";
import React, { useState } from "react";
const R = require("rambda");
const { BACKEND_URL } = require("../../../components/constants");

const formItemLayout = {
  labelCol: {
    span: 4,
  },
  wrapperCol: {
    span: 16,
  },
};

const imageDisplayer = (id, password) => {
  return (
    <Image
      src={`${BACKEND_URL}/${id}?password=${password}`}
      fallback="data:image/png;base64,"
    />
  );
};

export async function getServerSideProps({ params, query }) {
  if (R.isNil(query.password)) {
    return {
      props: {
        error: {
          state: true,
          message: "You need a password to access this image.",
        },
      },
    };
  }

  try {
    const res = await fetch(
      `${BACKEND_URL}/${params.id}?password=${query.password}`
    );

    if (res.status == 200) {
      return {
        props: {
          passwordVerification: true,
          error: null,
        },
      };
    } else {
      return {
        props: {
          error: {
            state: true,
            message: `Error Code ${res.status}, ${
              res.status === 401 ? "Check your password" : "Image not found"
            }`,
          },
        },
      };
    }
  } catch (err) {
    return {
      props: {
        error: {
          state: true,
          message: err.message,
        },
      },
    };
  }
}

const handleRedirect = (values, router, id) => {
  if (R.isNil(values.password)) {
    router.push(`/image/protected/${id}`);
  } else {
    router.push(`/image/protected/${id}?password=${values.password}`);
  }
};

const protectedPage = ({ passwordVerification, error }) => {
  const router = useRouter();
  const { id, password } = router.query;
  const [isPasswordCorrect, setAuthResult] = useState(
    passwordVerification ? passwordVerification : false
  );
  const [err, setError] = useState(
    error != null ? error : { state: false, message: "" }
  );

  return (
    <Layout>
      {!isPasswordCorrect && (
        <Row
          type="flex"
          align="middle"
          justify="center"
          style={{ minHeight: "100vh" }}
        >
          <Col type="flex" align="middle" justify="center" span={8}>
            <Form
              name="validate_other"
              {...formItemLayout}
              onFinish={(values) => handleRedirect(values, router, id)}
            >
              <Form.Item name="password" label="Password">
                <Input.Password placeholder="Password required to access protected image" />
              </Form.Item>

              <Form.Item
                wrapperCol={{
                  span: 12,
                  offset: 6,
                }}
              >
                <Button type="primary" htmlType="submit">
                  Submit
                </Button>
              </Form.Item>
            </Form>
            {err.state && <Alert type="error" message={err.message} banner />}
          </Col>
        </Row>
      )}

      {isPasswordCorrect && imageDisplayer(id, password)}
    </Layout>
  );
};

export default protectedPage;
